package br.com.generation.aula01;

public class ExemploVariavel02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var1;
		double var2;
		
		var1 = 10;
		var2 = 10.0;
		
		System.out.println("o valor da var1 = " + var1);
		System.out.println("o valor da var2 = " + var2);
		System.out.println();
		
		var1 = var1 / 4;
		var2 = var2 / 4;
		
		System.out.println("o valor da var1 = " + var1);
		System.out.println("o valor da var2 = " + var2);
	}

}
